schemas:
DEFINE TABLE category SCHEMALESS;

DEFINE FIELD title ON category TYPE string ASSERT $value != NONE;
DEFINE FIELD description ON category TYPE string;
DEFINE FIELD forums ON category TYPE array;
DEFINE FIELD created_at ON forum_user TYPE datetime VALUE time::now();

DEFINE TABLE forum SCHEMALESS;

DEFINE FIELD title ON forum TYPE string ASSERT $value != NONE;
DEFINE FIELD description ON forum TYPE string;
DEFINE FIELD slug ON forum TYPE string ASSERT $value != NONE;
DEFINE FIELD category ON forum TYPE record(category) ASSERT $value != NONE;
DEFINE FIELD created_at ON forum TYPE datetime VALUE time::now();
DEFINE TABLE main_forum SCHEMALESS;

DEFINE FIELD title ON main_forum TYPE string ASSERT $value != NONE;
DEFINE FIELD created_at ON main_forum TYPE datetime VALUE time::now();
DEFINE TABLE permission SCHEMALESS;

DEFINE FIELD description ON permission TYPE string ASSERT $value != NONE;
DEFINE FIELD value_kind ON permission TYPE string ASSERT $value IN ['Boolean', 'Integer', 'Float'];
DEFINE TABLE post SCHEMALESS;

DEFINE FIELD content ON post TYPE string ASSERT $value != NONE;
DEFINE FIELD thread ON post TYPE record(thread) ASSERT $value != NONE;
DEFINE FIELD created_by ON post TYPE record(user) ASSERT $value != NONE;
DEFINE FIELD created_at ON post TYPE datetime VALUE time::now();
DEFINE TABLE thread SCHEMALESS;

DEFINE FIELD title ON thread TYPE string ASSERT $value != NONE;
DEFINE FIELD slug ON thread TYPE string ASSERT $value != NONE;
DEFINE FIELD content ON thread TYPE string ASSERT $value != NONE;
DEFINE FIELD sticky ON thread TYPE bool VALUE $value OR false;
DEFINE FIELD locked ON thread TYPE bool VALUE $value OR false;
DEFINE FIELD forum ON thread TYPE record(forum) ASSERT $value != NONE;
DEFINE FIELD created_by ON thread TYPE record(user) ASSERT $value != NONE;
DEFINE FIELD created_at ON thread TYPE datetime VALUE time::now();
DEFINE TABLE user SCHEMALESS;

DEFINE FIELD username ON user TYPE string ASSERT $value != NONE;
DEFINE FIELD email ON user TYPE string ASSERT $value != NONE AND is::email($value);
DEFINE FIELD avatar_url ON user TYPE string;
DEFINE FIELD user_group ON user ASSERT $value != NONE;
DEFINE FIELD password ON user TYPE string ASSERT $value != NONE;
DEFINE FIELD created_at ON user TYPE datetime VALUE time::now();

DEFINE INDEX username ON TABLE user COLUMNS username UNIQUE;
DEFINE INDEX email ON TABLE user COLUMNS email UNIQUE;
DEFINE TABLE user_group SCHEMALESS;

DEFINE FIELD name ON user_group TYPE string ASSERT $value != NONE;
DEFINE FIELD user_title ON user_group TYPE string ASSERT $value != NONE;
DEFINE FIELD description ON user_group TYPE string;
DEFINE FIELD permissions ON user_group TYPE array;

DEFINE INDEX name_idx ON TABLE user_group COLUMNS name UNIQUE;
